import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

import { PostInterface } from '../interfaces/Post';

@Injectable({
  providedIn: 'root'
})
export class PostsService {

  constructor(private http: HttpClient) {
  }

  getAllPosts(): Observable<PostInterface[]> {
    const url = 'http://localhost:3030/data/cars';
    return this.http.get<PostInterface[]>(url);
  }

  getPostById (postId: string): Observable<PostInterface> {
    const url = `http://localhost:3030/data/cars/${postId}`;
    return this.http.get<PostInterface>(url);
  }

  //Opit da se napravi edit buton

  editPost(episodeId: string, episodeData: PostInterface): Observable<PostInterface> {
    const url = `http://localhost:3030/data/cars/${episodeId}`;
    return this.http.put<PostInterface>(url, episodeData);
  }
}
