import { Component, OnDestroy, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Title } from '@angular/platform-browser';
import { Router, ActivatedRoute } from '@angular/router';
import { Subscription } from 'rxjs';

import { PostInterface } from 'src/app/core/interfaces/Post';
import { PostsService } from 'src/app/core/services/posts.service';

@Component({
  selector: 'app-edit',
  templateUrl: './edit.component.html',
  styleUrls: ['./edit.component.css']
})
export class EditComponent implements OnInit, OnDestroy {

  post!: PostInterface;
  subscribe$!: Subscription;
  errorMessage!: string;

  constructor(
    private PostService: PostsService,
    private titleService: Title,
    private router: Router,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.titleService.setTitle('Edit Page');
    const postId = this.route.snapshot.params['postId'];
    this.subscribe$ = this.PostService.getPostById(postId).subscribe({
      next: (postInfo) => this.post = postInfo,
      error: (error) => this.errorMessage = error.error.message
    });
  }

  editHandler(formData: NgForm) {
    const postId = this.route.snapshot.params['postId'];

    this.subscribe$ = this.PostService.editPost(postId, formData.value).subscribe({
      error: (error) => {
        if (error.message.includes('Unknown Error')) {
          this.errorMessage = 'Server not connected!'
        } else {
          this.errorMessage = error.error.message;
        }
      },
      complete: () => this.router.navigate([ '/cars', 'details', `${postId}`])
    });
  }

  ngOnDestroy(): void {
    if (this.subscribe$ != undefined) {
      this.subscribe$.unsubscribe();
    }
  }


}
<div class="catalogWrapper">

    <main class="aadd-main">
        <div class="addContainer">
            <div class="registerLoginWrapper">
                <form *ngIf="post" class="md:space-y-6" #editForm="ngForm" (ngSubmit)="editHandler(editForm)" ngClass="editForm">
                    <h1 class="registerLoginTitle">Edit Car</h1>
                    <div class="input-box">
                        <input   ngModel #brand="ngModel" [(ngModel)]="post.brand" name="brand"  type="text" placeholder="Brand" required min>
                        <i class="fa-solid fa-car"></i>

                        <ng-container *ngIf="brand.invalid && brand.touched">
                            <div class="ng-container-msg" *ngIf="brand.errors?.['required']">Name is required
                            </div>
                            <div class="ng-container-msg" *ngIf="brand.errors?.['minlength']">Name must be at least 4 characters long
                            </div>
                        </ng-container>

                    </div>

                    <div class="input-box">
                        <input ngModel #model="ngModel" [(ngModel)]="post.model" name="model" type="text" placeholder="Model" required>
                        <i class="fa-solid fa-car"></i>
                    </div>

                    <div class="input-box">
                        <input gModel #imageUrl="ngModel" [(ngModel)]="post.imageUrl" name="imageUrl" type="url" placeholder="Image" required>
                        <i class="fa-solid fa-image"></i>
                    </div>

                    <div class="input-box">
                        <input ngModel #year="ngModel" [(ngModel)]="post.year" name="year" type="number" placeholder="Year" required>
                        <i class="fa-regular fa-calendar"></i>
                    </div>

                    <div class="input-box">
                        <input ngModel #description="ngModel" [(ngModel)]="post.description" name="description" type="text" maxlength="150" placeholder="Description" required>
                        <i class="fa-solid fa-pen"></i>
                    </div>


                    <!-- Тука не го разбирам напълно тоя editForm -->
                    <button  type="submit" class="btn">Edit your car</button>
                </form>
            </div>
        </div>
    </main>
</div>
